                                                           pc_dbd_id,
                                                           vn_contract_pc_charges,
                                                           vc_contract_pc_cur_id);
        
          if vc_contract_pc_cur_id <> cur_grd_rows.base_cur_id then
            -- Bank FX Rate from Penalty to Base Currency
            pkg_general.sp_get_base_cur_detail(vc_contract_pc_cur_id,
