           psu_id,
           corporate_id,
           corporate_name,
           internal_gmr_ref_no,
           internal_contract_item_ref_no,
           contract_ref_no,
           delivery_item_no,
           del_distribution_item_no,
           product_id,
           product_name,
           origin_id,
