         vc_price_cur_id,
         vc_price_cur_code,
         vc_price_weight_unit,
         vc_price_weight_unit_id,
         vc_price_qty_unit,
         null,
         null,
         vc_price_basis,
         vc_price_fixation_status,
         'Not Applicable',
         cur_pcdi_rows.payment_due_date,
         vn_contract_base_price_unit_id,
         vn_fw_exch_rate_price_to_base,
         vc_price_description,
