             sum(case
                   when pa.tranascation_type = 'Calculated' then
                    pa.rcharges_amount
                   else
                    0
                 end) - sum(case
                              when pa.tranascation_type = 'Invoiced' then
                               pa.rcharges_amount
                              else
                               0
                            end) rcharges_amount,
             sum(case
                   when pa.tranascation_type = 'Calculated' then
                    pa.penalty_amount
                   else
                    0
                 end) - sum(case
                              when pa.tranascation_type = 'Invoiced' then
                               pa.penalty_amount
                              else
                               0
                            end) penalty_amount,
             sum(case
                   when pa.tranascation_type = 'Calculated' then
                    pa.payable_amt_pay_ccy
                   else
                    0
                 end) - sum(case
                              when pa.tranascation_type = 'Invoiced' then
                               pa.payable_amt_pay_ccy
                              else
                               0
                            end) payable_amount_pay_ccy,
             sum(case
                   when pa.tranascation_type = 'Calculated' then
                    pa.payable_amt_price_ccy
                   else
                    0
                 end) - sum(case
                              when pa.tranascation_type = 'Invoiced' then
                               pa.payable_amt_price_ccy
                              else
                               0
                            end) payable_amount_price_ccy,
             pa.pay_in_cur_id,
             pa.pay_in_cur_code,
             0,
             sum(case
                   when pa.tranascation_type = 'Calculated' then
                    pa.othercharges_amount
                   else
                    0
                 end) - sum(case
                              when pa.tranascation_type = 'Invoiced' then
                               pa.othercharges_amount
                              else
                               0
                            end) othercharges_amount,
             'Difference'
        from pa_purchase_accural_gmr pa
       where pa.process_id = pc_process_id
         and pa.corporate_id = pc_corporate_id
       group by pa.corporate_id,
                pc_process_id,
                pa.product_id,
                pa.product_type,
                pa.contract_type,
                pa.cp_id,
                pa.counterparty_name,
                pa.gmr_ref_no,
                pa.element_id,
                pa.element_name,
                pa.payable_returnable_type,
                pa.assay_content_unit,
                pa.payable_qty_unit_id,
                pa.pay_in_cur_id,
                pa.pay_in_cur_code;
  
    commit;
  
    sp_eodeom_process_log(pc_corporate_id,
                          pd_trade_date,
                          pc_process_id,
                          1004,
                          'sp_phy_purchase_accural End');
  end;

