       temp_gmr_invoice        t
 where grd.process_id = pc_process_id
   and grd.parent_internal_grd_ref_no = t.stock_id
   and grd.process_id = pc_process_id
   and grd.status = 'Active'
   and grd.is_deleted ='N'
