         and grd.tolling_stock_type = 'None Tolling'
      --
      -- Purchase GMR Shipped But Not TT Query End
      --
      -- Section 2
      -- Sales GMR Shipped But Not TT Starts Here
      --
      union all
      select pc_corporate_id,
             pc_process_id,
             cs.internal_cost_id,
